import React from 'react'
import '../../Styles/Generic/Generic.css'

const Shops = [
    {
        id:'1',
        name:'JJ Bean',
        area: 'Commercial Drive',
        route: '/cafes/1'
    },
    {
        id:'2',
        name:'Elysian',
        area:'Mount Pleasant',
        route: '/cafes/2'
    }
]

class SearchArea extends React.Component {
    constructor(props){
        super(props);

            this.state= {
                Shops: Shops,
                term: ''
            }
            this.searchHandler = this.searchHandler.bind(this)

    }
    searchHandler(event) {
        this.setState({ term: event.target.value})
    }
    render () {
        return (
            <div className='search'>
            <div className='search__container'>
                <form>
                    <input className='search__input' type='text' onChange={this.searchHandler}/>
                </form>
                {
                    this.state.Shops.map(function(shop){
                        return(
                            <div key={shop.id}>
                                <h1>{shop.name}</h1>
                                <a href={shop.route} >Shop</a>
                            </div>
                        )
                    })
                }
            </div>
            </div>
        )
    }
}
export default SearchArea
