function searchingFor(term) {
  return function(x) {
    return x.area.toLowerCase().includes(term.toLowerCase()) || !term
  }
}

class SearchArea extends React.Component {
  popOver() {
    return (
      <div>
        <Button id='popover-button' type='button'>
          SEARCH TIPS
        </Button>
        <UncontrolledPopover trigger='focus' placement='bottom' target='popover-button'>
          <PopoverHeader>Search for</PopoverHeader>
          <PopoverBody>
            Focusing on the trigging element makes this popover appear. Blurring (clicking away)
            makes it disappear. You cannot select this text as the popover will disappear when you
            try.
          </PopoverBody>
        </UncontrolledPopover>
      </div>
    )
  }
  constructor(props) {
    super(props)

    this.state = {
      Shops: Shops,
      term: ''
    }
    this.searchHandler = this.searchHandler.bind(this)
  }
  searchHandler(event) {
    this.setState({ term: event.target.value })
  }
  render() {
    const { term, Shops } = this.state
    return (
      <div className='search'>
        <div>{this.popOver()}</div>
        <div className='search__container'>
          <input
            class='searchbar'
            type='text'
            onChange={this.searchHandler}
            value={term}
            placeholder='Search...'
          />
          <a id='btnSearch' class='btn-search'>
            <i class='fa fa-search' />
          </a>
        </div>
        <div className='search__container__two'>
          {Shops.filter(searchingFor(term)).map(shop => {
            return (
              <a className='search__shops' key={shop.id} href={shop.route}>
                <div className='search__shops__route'>
                  <h1 className='search__shop-name'>{shop.name}</h1>
                </div>
              </a>
            )
          })}
        </div>
      </div>
    )
  }
}
export default SearchArea
