import React from 'react'
import '../../Styles/Search/Search.css'

class Search extends React.Component {
  state = { searchString: '' }
  handleChange = e => {
    this.setState({ searchString: e.target.value })
  }
  render() {
    var shops = this.props.items,
      searchString = this.state.searchString.trim().toLowerCase()
    if (searchString.length > 0) {
      shops = shops.filter(function(i) {
        return i.name.toLowerCase().match(searchString)
      })
    }
    return (
      <div>
        <form id='demo-2'>
          <input
            type='text'
            value={this.state.searchString}
            onChange={this.handleChange}
            placeholder='Type here...'
          />
          <ul>
            {shops.map(function(i) {
              return (
                <li>
                   <a href={i.route}>{i.name}</a>
                </li>
              )
            })}
          </ul>
        </form>
      </div>
    )
  }
}

// Constant, library
var shops = [
  { name: 'Backbone.js', route: 'cafes/4' },

]

// put input and display on page
React.render(<Search items={shops} />, document.getElementById('content'))


var FilteredList = React.createClass({
    filterList: function(event){
      var updatedList = this.state.initialItems;
      updatedList = updatedList.filter(function(item){
        return item.toLowerCase().search(
          event.target.value.toLowerCase()) !== -1;
      });
      this.setState({items: updatedList});
    },
    getInitialState: function(){
       return {
         initialItems: [
           "Apples",
           "Broccoli",
           "Chicken",
           "Duck",
           "Eggs",
           "Fish",
           "Granola",
           "Hash Browns"
         ],
         items: []
       }
    },
    componentWillMount: function(){
      this.setState({items: this.state.initialItems})
    },
    render: function(){
      return (
        <div className="filter-list">
          <form id='demo-2'>
          <fieldset className="form-group">
          <input type="text" className="form-control form-control-lg" placeholder="Search" onChange={this.filterList}/>
          </fieldset>
          </form>
        <List items={this.state.items}/>
        </div>
      );
    }
  });

  var List = React.createClass({
    render: function(){
      return (
        <ul className="list-group">
        {
          this.props.items.map(function(item) {
            return <li className="list-group-item" data-category={item} key={item}>{item}</li>
          })
         }
        </ul>
      )  
    }
  });

  React.render(<FilteredList/>, document.getElementById('content'));
