import React from 'react'
import { Card, Button, CardTitle, CardText, Row, Col } from 'reactstrap'
import '../../Styles/HomeCards/HomeCards.css'
import axios from 'axios'

const url = 'http://localhost:8080/shops'

class Cafes extends React.Component {
  state = {
    shops: [],
    id: ''
  }
  getCafes = () => {
    axios.get(url).then(response => {
      this.setState({
        shops: response.data
      })
      console.log(response.data.inventory)
    })
  }
  componentDidMount() {
    this.getCafes()
  }

  render() {
    const cafeList = this.state.shops.map((cafe, i) => {
      return (
        <div>
          <tr key={i}>
            <td>
              <h1>{cafe.inventory.name}</h1>
              <br />
            </td>
            <td>{cafe.long_description}</td>
          </tr>
        </div>
      )
    })
    return <div>{cafeList}</div>
  }
}

export default Cafes
