import React from 'react'
import '../../Styles/DropDownN/DropDownN.css'
import { DropdownItem } from 'reactstrap'

class CollapsibleShops extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      open: false
    }
    this.togglePanel = this.togglePanel.bind(this)
  }

  togglePanel(e) {
    this.setState({ open: !this.state.open })
  }

  componentDidUpdate() {
    // this.props.onToggle(this.props.index);
  }

  render() {
    return (
      <div>
        <div onClick={e => this.togglePanel(e)} className='header'>
          <div className='dropdown__icon' />
          <h1>EXPLORE</h1>
        </div>
        {this.state.open ? <div className='content'>{this.props.children}</div> : null}
      </div>
    )
  }
}

class DropDownShops extends React.Component {
  constructor(props) {
    super(props)
  }
  render() {
    return (
      <div className='dropdown__container'>
        <CollapsibleShops>
          <div>
<DropdownItem href='/artists'>ARTISTS</DropdownItem>
<DropdownItem href='/cafes'>CAFES</DropdownItem>
<DropdownItem href='/drinks'>DRINKS</DropdownItem>
<DropdownItem href='/food'>FOOD</DropdownItem>
<DropdownItem href='/grocery'>GROCERY</DropdownItem>
<DropdownItem href='/shops'>SHOPS</DropdownItem>
          </div>
        </CollapsibleShops>
      </div>
    )
  }
}
