import React from 'react'
import { Card, Button, CardTitle, CardText, Row, Col } from 'reactstrap'
import '../../Styles/HomeCards/HomeCards.css'
import axios from 'axios'

const url = 'http://localhost:8080/shops'

class Cafes extends React.Component {
  state = {
    shops: [],
    id: ''
  }
  getCafes = () => {
    axios.get(url).then(response => {
      this.setState({
        shops: response.data

      })
      console.log(response.data)
    })
  }
  componentDidMount() {
    this.getCafes()
  }

  render() {
    const cafeList = this.state.shops.inventory[0].map((cafe, i) => {
      return (
        <div>
          <Row id='row'>
            <Col id='card' sm='6'>
              <Card body>
                <CardTitle>{cafe.inventory.name}</CardTitle>
                <CardText>
                  {cafe.inventory.long_description}
                </CardText>
                <Button>Go somewhere</Button>
              </Card>
            </Col>
          </Row>
        </div>
      )
  })
return(
  <div>
    {cafeList}
  </div>
)
}
}

export default Cafes
