import React, { Component } from 'react'
import { Map, GoogleApiWrapper } from 'google-maps-react'
import { InfoWindow, Marker } from 'google-maps-react'
import axios from 'axios'
const url = 'http://localhost:8080/shops'

const mapStyles = {
  width: '100%',
  height: '100%'
}

export class ShopMap extends Component {
  state = {
   showingInfoWindow: false,
   activeMarker: {},
   selectedPlace: {}
  }
  getCafes = () => {
    axios.get(url).then(response => {
      this.setState({
        shops: response.data[0].inventory
      })
      console.log(response.data)
    })
  }
  componentDidMount() {
    this.getCafes()
  }
  render() {
    return (
      <Map
        google={this.props.google}
        zoom={12.5}
        style={mapStyles}
        initialCenter={{
          lat: 49.246292,
          lng: -123.116226
        }}
      >
        <Marker onClick={this.onMarkerClick} name={'Kenyatta International Convention Centre'} />
        <InfoWindow
          marker={this.state.activeMarker}
          visible={this.state.showingInfoWindow}
          onClose={this.onClose}
        >
          <div>
            <h4>{this.state.selectedPlace.name}</h4>
          </div>
        </InfoWindow>
      </Map>
    )
  }
}

export default GoogleApiWrapper({
  apiKey: 'AIzaSyCtKMlz5bz3VXsNVvtf8Ivq8H-_vEuuY_w'
})(ShopMap)
