import React from 'react'
import '../../Styles/Generic/Generic.css'
import { Button } from 'reactstrap'
import Prismic from 'prismic-javascript'
import {Link, RichText, Date} from 'prismic-reactjs';

class Cafes extends React.Component {
  state = {
    cafes: []  
}
  componentWillMount() {
    const apiEndpoint = 'https://localify.cdn.prismic.io/api/v2'

    Prismic.api(apiEndpoint).then(api => {
      api.query(Prismic.Predicates.at('document.type', 'vendors')).then(response => {
        if (response) {
          this.setState({ cafes: response.results[0]})
          console.log(response.results[0])
        }
      })
    })
  }
  render() {
    if (this.state.cafes) {
        const cafes = this.state.cafes.data;
    return (
        <div>
          <div>
            <div id='generic__card'>
              <h1>{RichText.asText(cafes.vendors.title)}</h1>
              <div className='generic__image'/>
              <div className='generic__lower__card'>
                <p>H</p>
                <p>H</p>
                <p>H</p>
                <Button>WEBSITE</Button>
              </div>
            </div>
          </div>
        </div>
      )

    }
    return <h1>Loading...</h1>;
  }
}


export default Cafes
