import React, { Component } from 'react'
import axios from 'axios'
import {
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  Marker,
  InfoWindow,
  GoogleApiWrapper
} from 'google-maps-react'
const url = 'http://localhost:8080/shops'

class ShopMap extends React.Component {
  state = {
    shops: [],
    id: ''
  }
  getCafes = () => {
    axios.get(url).then(response => {
      this.setState({
        shops: response.data[0].inventory
      })
      console.log(response.data)
    })
  }
  componentDidMount() {
    this.getCafes()
  }


  render() {
    return (
      <GoogleMap defaultZoom={8} defaultCenter={{ lat: 29.5, lng: -95 }}>
        {this.state.shops.map(marker => (
          <Marker position={{ lat: marker.latitude, lng: marker.longitude }} key={marker.id} />
        ))}
      </GoogleMap>
    )
  }
}
export default GoogleApiWrapper(props => ({
  apiKey: 'AIzaSyCtKMlz5bz3VXsNVvtf8Ivq8H-_vEuuY_w'
}))(ShopMap)
