import React from 'react'
import { Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap'
import '../../../Styles/DropDownNav/DropDownNav.css'
import Modal from '../AboutModal/AboutModal'
import ContactModal from '../ContactModal/ContactModal'
import '../../../Styles/ModalStyles/ModalStyles.css'

class DropDown extends React.Component {
  constructor(props) {
    super(props)

    this.toggle = this.toggle.bind(this)
    this.state = {
      dropdownOpen: false
    }
  }

  toggle() {
    this.setState(prevState => ({
      dropdownOpen: !prevState.dropdownOpen
    }))
  }
  state = {
    show: false
  }
  showModal = e => {
    this.setState({
      show: !this.state.show
    })
  }

  render() {
    return (
      <Dropdown id='dropdown nav' isOpen={this.state.dropdownOpen} toggle={this.toggle}>
        <DropdownToggle id='dropdown nav' caret>
          INFO
        </DropdownToggle>
        <DropdownMenu>
          <div>
            <Modal />
          </div>
          <div>
            <ContactModal />
          </div>
          <div>
            <JoinModal />
          </div>
          <button
            class='toggle-button'
            id='centered-toggle-button'
            onClick={e => {
              this.showModal(e)
            }}
          >
            {' '}
            show Modal{' '}
          </button>
          <JoinModal onClose={this.showModal} show={this.state.show}>
          Lorem ipsum dolor sit amet, consectetur adipisicing elit. Nobis
          deserunt corrupti, ut fugit magni qui quasi nisi amet repellendus non
          fuga omnis a sed impedit explicabo accusantium nihil doloremque
          consequuntur.
        </JoinModal>
          {/* <DropdownItem href="/join">BE A PART OF US</DropdownItem> */}
        </DropdownMenu>
      </Dropdown>
    )
  }
}
export default DropDown
